mapper.select:
SELECT i_rest, nm, addr, lat, lng, i_category, menu_pic, hit, r_dt, m_dt, phone, price_range, open_time, close_time, ctnt, i_district
FROM t_rest

	WHERE i_rest = #{i_rest}

 	SELECT i_rest, nm, addr, lat, lng, i_category, i_district
 	FROM t_rest


 			WHERE nm like #{sql_search_text} or addr like #{sql_search_text}


 			WHERE lat BETWEEN #{sw_lat} AND #{ne_lat}
			AND lng BETWEEN #{sw_lng} AND #{ne_lng}


 	ORDER BY i_rest

mapper.update:
UPDATE t_review SET ctnt = #{ctnt}
WHERE i_user = #{i_user}
AND seq = #{seq}

mapper.delete:
DELETE FROM t_review
WHERE i_user = #{i_user}
AND seq = #{seq}
